package com.syys;

import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.util.Base64;

/**
 * @author yangsong
 * @version 1.0
 * @date 2022/10/9 18:47
 */
public class Base64ConverterUtils {


    public static void main(String[] args) {
        String pdfStr = "";
        Base64Converter(pdfStr,"D://sds.pdf");
    }

    public static void Base64Converter(String encodedBytes, String tempPath) {
        // BASE64Decoder decoder = new BASE64Decoder();


        byte[] decodedBytes;
        try {
            decodedBytes = Base64.getDecoder().decode(encodedBytes); // decoder.decodeBuffer(encodedBytes);
            File file = new File(tempPath);
            FileOutputStream fop = new FileOutputStream(file);
            fop.write(decodedBytes);
            fop.flush();
            fop.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

}